var api="http://api.programmazione.me/";function like(){$("article > div.item-tools > a:first-child").click(function(c){c.preventDefault();var a=$(this).parents("article").attr("id");var b=$(this);$.ajax({type:"POST",url:api+"like/",xhrFields:{withCredentials:true},dataType:"json",data:{id:a},success:function(d){span=b.next();votesCount=Number(span.text());switch(d){case 1:votesCount++;b.addClass("active");break;case 2:votesCount--;b.removeClass("active");break;case 0:break;case -1:break}span.text(votesCount)}})})}function star(){var a=$("a.btn-star");a.click(function(c){c.preventDefault();var b=$(this).attr("id");$.ajax({type:"POST",url:api+"star/",xhrFields:{withCredentials:true},dataType:"json",data:{id:b},success:function(d){if(d[0]){switch(d[1]){case 1:a.addClass("active");break;case 2:a.removeClass("active");break}}else{alert(d[1])}}})})}$(document).ready(function(){like();star()});