$(document).ready(function(){$("article > div.item-tools > a:first-child").click(function(){event.preventDefault();var a=$(this).parents("article").attr("id");var b=$(this);$.ajax({type:"POST",url:"http://ajax.programmazione.me/like/",xhrFields:{withCredentials:true},dataType:"json",data:{id:a},success:function(c){span=b.next();votesCount=Number(span.text());switch(c){case 1:votesCount++;b.addClass("active");break;case 2:votesCount--;b.removeClass("active");break;case 0:break;case -1:break}span.text(votesCount)}})})});