var api="http://api.programmazione.me/";function addFriend(){$("article > section.item-content > div > div > button.btn-icon").click(function(){event.preventDefault();var a=$(this).parents("article").attr("id");var b=$(this);$.ajax({type:"POST",url:api+"addfriend/",xhrFields:{withCredentials:true},dataType:"json",data:{id:a},success:function(c){if(c[0]){switch(c[1]){case 1:b.text("CONNECTED");break;case 2:b.text("UNCONNECTED");break}}else{alert(c[1])}}})})}$(document).ready(function(){addFriend()});