var api="http://reindex.local/api/";function addFriend(a){$("article > section.item-content > div > div > button.btn-icon").click(function(d){d.preventDefault();var b=$(this).parents("article").attr("id");var c=$(this);$.ajax({type:"POST",url:api+"member/add-friend/",xhrFields:{withCredentials:true},dataType:"json",data:{id:b},success:function(e){if(e[0]){switch(e[1]){case 1:c.text("CONNECTED");break;case 2:c.text("UNCONNECTED");break}}else{alert(e[1])}}})})}$(document).ready(function(a){addFriend(a)});