var api="http://programmazione.me/api/";function addFriend(){$("article > section.item-content > div > div > button.btn-icon").click(function(c){c.preventDefault();var a=$(this).parents("article").attr("id");var b=$(this);$.ajax({type:"POST",url:api+"addfriend/",xhrFields:{withCredentials:true},dataType:"json",data:{id:a},success:function(d){if(d[0]){switch(d[1]){case 1:b.text("CONNECTED");break;case 2:b.text("UNCONNECTED");break}}else{alert(d[1])}}})})}$(document).ready(function(){addFriend()});