function like(){$("article > section.item-content > ul.item-buttons > li > button.btn-like").click(function(){event.preventDefault();var a=$(this).parents("article").attr("id");var b=$(this);$.ajax({type:"POST",url:"http://ajax.programmazione.me/like/",xhrFields:{withCredentials:true},dataType:"json",data:{id:a},success:function(c){score=b.closest("ul").find("button.score");votesCount=Number(score.text());switch(c){case 1:votesCount++;b.addClass("active");break;case 2:votesCount--;b.removeClass("active");break;case 0:break;case -1:break}score.text(votesCount)}})})}function star(){$("button.btn-star").click(function(){event.preventDefault();var a=$("article").attr("id");var b=$(this);$.ajax({type:"POST",url:"http://ajax.programmazione.me/star/",xhrFields:{withCredentials:true},dataType:"json",data:{id:a},success:function(c){switch(c){case 1:b.addClass("active");break;case 2:b.removeClass("active");break;case -1:break}}})})}$(document).ready(function(){like();star()});